// NUmeros
function hasNumber(myString) {
  return /\d/.test(myString);
}

let count = 0;
journaleux.forEach(journaliste => {
	if (hasNumber(journaliste)) {
		count++;
	}
});
console.log(`Nombre d'handles contenant un numero : ${count}`);


// Aude
let count2 = 0
journaleux.forEach(journaliste => {
	if (journaliste.indexOf('aude') > -1 || journaliste.indexOf('Aude') > -1) {
		count2++;
	}
});
console.log(`Nombre d'handles contenant 'aude' : ${count2}`);


// Aude bis
let count3 = 0
journaleux.forEach(journaliste => {
	if (journaliste.includes('aude') || journaliste.includes('Aude')) {
		count3++;
	}
});

// Majuscule premier place
let maj = 0
journaleux.forEach(journaliste => {
	if (journaliste[1] === journaliste[1].toUpperCase()) {
		maj++;
	}	
});
console.log(`Nombre d'handles commencant par une majuscule : ${maj}`);


// Majuscule any place
let maj2 = 0;

journaleux.forEach(journaliste => {
	for(let index = 1; index < journaliste.length; index++) {
		if (journaliste[index] === journaliste[index].toUpperCase()) {
			maj2++;
			break;
		}
	}
});
console.log(`Nombre d'handles contenant par une majuscule : ${maj2}`);



// Underscore
let underscore = 0;
journaleux.forEach(journaliste => {
	if (journaliste.includes('_')) {
		underscore++;
	}
});
console.log(`Nombre d'handles contenant _ : ${underscore}`);



// Par ordre alphabetique
// Creation nouvelle array pour ne pas modifier l'originale
let sortJournaleux = [];
journaleux.forEach(journaliste => {
	sortJournaleux.push(journaliste);
});

// Trie
sortJournaleux.sort(function (a, b) {
    var x = a.toUpperCase(),
        y = b.toUpperCase();
    if (x > y) {
        return 1;
    }
    if (x < y) {
        return -1;
    }
    return 0;
});
console.log('Journaleux par ordre alphabetique :')
