// Les 50 plus courts
// Creation nouvelle array pour ne pas modifier l'originale
let shortJournaleux = [];
journaleux.forEach(journaliste => {
	shortJournaleux.push(journaliste);
});

// Trie
shortJournaleux.sort(function (a, b) {
  return a.length - b.length;
});
